function [h1, h2, VM] = Fit_logit(RunA, RunB, tc, t)% [h1, h2, VM] = Fit_logit(RunA, RunB, tc, t)% Fit inverse logit function model to time course% Initial valuesV0 = [ 0.5 5 1 0.5 25 -1.5 0.5 50];V02 = [V0 V0]';% Find optimal valuesoptions = optimset('MaxFunEvals',100000,'Maxiter',10000000,'Display','Final');VM = fminsearch(@msq_timecourse,V02,options,t,tc,RunA,RunB );% Use optimal values to fit hemodynamic response functionsh1 = il_hdmf_tw(t,VM(1:8));h2 = il_hdmf_tw(t,VM(9:16));return;